xpath.script:
document.addEventListener("DOMContentLoaded", function() {
// Get the password and confirm password fields and the message span
var passwordField = document.querySelector("input[name='password']");
var confirmPasswordField = document.querySelector("input[name='confirm_password']");
var confirmMsg = document.getElementById("confirm_password_msg");
if (passwordField) {
if (confirmPasswordField) {
if (confirmMsg) {
function checkPasswords() {
var password = passwordField.value;
var confirmPassword = confirmPasswordField.value;
// Check if the values are identical
if (password === confirmPassword) {
confirmMsg.textContent = "Passwords match ✅";
confirmMsg.style.color = "green";
} else {
confirmMsg.textContent = "Passwords do not match ❌";
confirmMsg.style.color = "red";
}
}
// Listen for changes in both fields
passwordField.addEventListener("input", checkPasswords);
confirmPasswordField.addEventListener("input", checkPasswords);
}
}
}

});
